mapper.select:
select id, username, password, email, phone, question, answer, role, create_time,
update_time
from neuedu_user where username=#{username} and password = #{password}

select id, username, password, email, phone, question, answer, role, create_time,update_time
from neuedu_user
where token = #{token}

mapper.update:
update neuedu_user
set token=null
where id = #{id,jdbcType=INTEGER}
